//takes stats as array of base stats and returns actualized stats
export const createPokemonStats = (stats) => {

    let statAdder = Math.floor(Math.random() * 20 + 1) 
    console.log('STAT ADDER');
    console.log(statAdder);

    stats.map(stat => stat + statAdder)
}

console.log();
