import React, { Component, useEffect, useState } from "react";
import axios from "axios";

export default function PokeStorePage(props) {
  const { pokedex } = props;

  const [pokemonJSON, setPokemonJSON] = useState(pokemonModel) 
  console.log('POKEMON JSON');
  console.log(pokemonJSON);

  useEffect(() => {
    // const pokedexCopy = [...pokedex]
    pokedex.map( async (url) => {
       await fetchJSON(url)

       setPokemonJSON()
});
}, [pokedex]);

const fetchJSON = (url) => {
axios.get(url).then(response => {
    const newPokemon = {
        pokeName: response.data.name,
        base_experience: response.data.base_experience   
    }
    return newPokemon;
})
}

  //functions to handle: openPokemonModal, filter/search, buyPokemon, buyCoins (open a modal on store page? or buy coins in profile?)

  //render our store page with all pokemon
  return (
    <>
      <div>Pokemon Store</div>
      {pokemonJSON.map(pokemon => {
          return ( 
              <>
          <p>{pokemon.pokeName}</p>
          <div>{pokemon.base_experience}</div>
          </>
          )
      })}

    </>
  );
}


const pokemonModel = [
    {
    pokeName: 'test',
    base_experience: 1,
},
{
    pokeName: 'test 2',
    base_experience: 2,
}
]
