import axios from 'axios';

export default axios.create({
    baseURL: `https://pokeapi.co/api/v2`, 
    params: {
        offset: 0,
        limit: 384
    }
});


export const pokemonJSON = async (url) => {
    const response = await fetch(url)

    console.log('');
    console.log(response);
}

//limit: 384 is gens 1-3
